import logo from './logo.svg'
import React from 'react';
import './App.css';

function Numpad(props){

    console.log(props);

    return (
      <div className="numpad">

            <button id="AC" className="numpad__button" onClick = {() => props.handleInput("AC")}>AC</button>
            <button id="divide" className="numpad__button" onClick = {() => props.handleInput("/")}>/</button>
            <button id="multiply" className="numpad__button" onClick = {() => props.handleInput("*")}>*</button>



            <button id="seven" className="numpad__button" onClick = {() => props.handleInput(7)}>7</button>
            <button id="eight" className="numpad__button" onClick = {() => props.handleInput(8)}>8</button>
            <button id="nine" className="numpad__button" onClick = {() => props.handleInput(9)} >9</button>
            <button id="subtract" className="numpad__button" onClick = {() => props.handleInput("-")}>-</button>



            <button id="four" className="numpad__button" onClick = {() => props.handleInput(4)}>4</button>
            <button id="five" className="numpad__button" onClick = {() => props.handleInput(5)}>5</button>
            <button id="six" className="numpad__button" onClick = {() => props.handleInput(6)}>6</button>
            <button id="addition" className="numpad__button" onClick = {() => props.handleInput("+")}>+</button>



            <button id="one" className="numpad__button" onClick = {() => props.handleInput(1)}>1</button>
            <button id="two" className="numpad__button" onClick = {() => props.handleInput(2)}>2</button>
            <button id="three" className="numpad__button" onClick = {() => props.handleInput(3)}>3</button>
            <button id="equals" className="numpad__button" onClick = {() => props.handleInput("=")}>=</button>


            <button id="zero" className="numpad__button" onClick = {() => props.handleInput(0)}>0</button>
            <button id="decimal" className="numpad__button" onClick = {() => props.handleInput(".")}>.</button>

      </div>
    );
}

function TopNum(props){
    return (
        <div className="display__input">{props.topNum}</div>
    );
}

function BottomNum(props){
    return (
        <div className="display__output">{props.bottomNum}</div>
    );
}

let topNum = "";
let bottomNum = 0;

function Display(props){

    console.log(props);

    topNum = props.operationString;

    if (props.outputNum === undefined) {
      bottomNum = 0;
    }
    else {

      if (props.outputNum[1] === "AC") {
        topNum = "";
        bottomNum = 0; 
      }
      else {
        bottomNum = props.outputNum; 
      }

    }



    return (
      <div className="display">

      <TopNum topNum = {topNum}/>
      <BottomNum bottomNum = {bottomNum}/>

      </div>
    );
}

let operationString = "";

let arrayOfKeystrokes = [];

let token = false; 

let firstNum = 0; 

console.log(eval("1+1*2+6"))

function Calculator(){

    const [displayNum, setDisplayNum] = React.useState()
    const [operationStr, setOperationStr] = React.useState();

    console.log(operationStr);

    //This is the function that does the calculation and returns the output!

    function handleCalc(num, operation) {
      console.log(num);
      console.log(operation);
      try {

        if (num[1] == "=") {
          console.log("This shit fired");
          operation = operation.replace("=", "")
          console.log(operation);
          //console.log(eval(operation));
          setDisplayNum(eval(operation));

          console.log(operation + "=" + eval(operation))
          setOperationStr(operation + "=" + eval(operation));
        }

      }
      catch (error) {
        console.log("undefined issue");
      }


    }

    //This is the useEffect hook that handles the calculation!;

    React.useEffect(() => {
      handleCalc(displayNum, operationString);
    }, [displayNum, operationString])

    //This creates the operation String!

    function operation (symbol) {

      let arrayOfOperations = ["/", "*", "-", "+", "="];

      console.log(operationString.length);

      //operationString[operationString.length]

      if (arrayOfOperations.includes(operationString[operationString.length-1]) && arrayOfOperations.includes(symbol)) {

        operationString = operationString.slice(0, operationString.length-1);
        operationString = operationString + symbol;
        setOperationStr(operationString);
      }
      else if (symbol != "AC") {
        operationString = operationString + symbol;
        setOperationStr(operationString);
      }
      else {
        operationString = "";
        setOperationStr(operationString);
      }

    }

    //This takes the input and returns the display number!

    function getInput(symbol) {

      if (symbol === "AC") {
        //console.log("This fired")
        arrayOfKeystrokes=[];
        let props = [arrayOfKeystrokes, "AC"]
        setDisplayNum(props);
      }
      else if (symbol === "/") {
        arrayOfKeystrokes = [];
        let props = [arrayOfKeystrokes, "/"]
        setDisplayNum(props);

      }
      else if (symbol === "*") {
        arrayOfKeystrokes = [];
        let props = [arrayOfKeystrokes, "X"]
        setDisplayNum(props);
      }
      else if (symbol === "-") {
        arrayOfKeystrokes = [];
        let props = [arrayOfKeystrokes, "-"]
        setDisplayNum(props);
      }
      else if (symbol === "+") {
        arrayOfKeystrokes = [];
        let props = [arrayOfKeystrokes, "+"]
        setDisplayNum(props);
      }
      else if (symbol === "=") {
        arrayOfKeystrokes = [];
        let props = [arrayOfKeystrokes, "="]
        setDisplayNum(props);
      }
      else if (Number.isInteger(symbol) === true) {
        arrayOfKeystrokes.push(symbol)
        let props = [arrayOfKeystrokes.join(''), ""];
        setDisplayNum(props); 
      }

      operation(symbol);

    }




    return (
      <div className="calculator">
        <Display operationString = {operationStr} outputNum = {displayNum}/>
        <Numpad handleInput = {getInput}/>
      </div>
    );
  }























function App() {
    return (
        <div className="App">
          <Calculator />
          <div id="credits">
            <p>Designed and Coded By Michael Galan</p>
          </div>
        </div>

    );
  }

export default App;
