import logo from './logo.svg'
import React from 'react';
import './App.css';

class Numpad extends React.Component {

  render() {

    console.log(this.props.handleInput)

    function handleNum(num) {
      console.log(num)
    }

    return (
      <div className="numpad">

            <button id="AC" className="numpad__button" onClick = {() => handleNum("AC")}>AC</button>
            <button id="divide" className="numpad__button" onClick = {() => handleNum("/")}>/</button>
            <button id="multiply" className="numpad__button" onClick = {() => handleNum("*")}>*</button>



            <button id="seven" className="numpad__button" onClick = {() => handleNum(7)}>7</button>
            <button id="eight" className="numpad__button" onClick = {() => handleNum(8)}>8</button>
            <button id="nine" className="numpad__button" onClick = {() => handleNum(9)} >9</button>
            <button id="subtract" className="numpad__button" onClick = {() => handleNum("-")}>-</button>



            <button id="four" className="numpad__button" onClick = {() => handleNum(4)}>4</button>
            <button id="five" className="numpad__button" onClick = {() => handleNum(5)}>5</button>
            <button id="six" className="numpad__button" onClick = {() => handleNum(6)}>6</button>
            <button id="addition" className="numpad__button" onClick = {() => handleNum("+")}>+</button>



            <button id="one" className="numpad__button" onClick = {() => handleNum(1)}>1</button>
            <button id="two" className="numpad__button" onClick = {() => handleNum(2)}>2</button>
            <button id="three" className="numpad__button" onClick = {() => handleNum(3)}>3</button>
            <button id="equals" className="numpad__button" onClick = {() => handleNum("=")}>=</button>


            <button id="zero" className="numpad__button" onClick = {() => handleNum(0)}>0</button>
            <button id="decimal" className="numpad__button" onClick = {() => handleNum(".")}>.</button>

      </div>
    );
  }
}

class Display extends React.Component {
  render() {



    return (
      <div className="display">
        <div className="display__input">0</div>
        <div className="display__output">0</div>
      </div>
    );
  }
}

class Calculator extends React.Component {
  render() {

    function getInput(num) {
      console.log(num);
    }


    return (
      <div className="calculator">
        <Display />
        <Numpad handleInput = {getInput}/>
      </div>
    );
  }
}

class App extends React.Component {
  render() {
    return (
        <div className="App">
          <Calculator />
          <div id="credits">
            <p>Designed and Coded By Michael Galan</p>
          </div>
        </div>

    );
  }
}

export default App;
