import logo from './logo.svg'
import React from 'react';
import './App.css';

class Numpad extends React.Component {
  render() {
    return (
      <div className="numpad">

            <button id="AC" className="numpad__button">AC</button>
            <button id="divide" className="numpad__button">/</button>
            <button id="multiply" className="numpad__button">*</button>



            <button id="seven" className="numpad__button">7</button>
            <button id="eight" className="numpad__button">8</button>
            <button id="nine" className="numpad__button">9</button>
            <button id="subtract" className="numpad__button">-</button>



            <button id="four" className="numpad__button">4</button>
            <button id="five" className="numpad__button">5</button>
            <button id="six" className="numpad__button">6</button>
            <button id="addition" className="numpad__button">+</button>



            <button id="one" className="numpad__button">1</button>
            <button id="two" className="numpad__button">2</button>
            <button id="three" className="numpad__button">3</button>
            <button id="equals" className="numpad__button">=</button>


            <button id="zero" className="numpad__button">0</button>
            <button id="decimal" className="numpad__button">.</button>

      </div>
    );
  }
}

class Display extends React.Component {
  render() {
    return (
      <div className="display">
        <div className="display__input">0</div>
        <div className="display__output">0</div>
      </div>
    );
  }
}

class Calculator extends React.Component {
  render() {
    return (
      <div className="calculator">
        <Display />
        <Numpad />
      </div>
    );
  }
}

class App extends React.Component {
  render() {
    return (
        <div className="App">
          <Calculator />
        </div>

    );
  }
}

export default App;
